    private void checkIn() {
        System.out.println(choice.getSelectedItem());
        System.out.println(username.getText());
        System.out.println(passwordField.getPassword());


        if (choice.getSelectedItem().equals(values[0]) && username.getText().equals("Mutahhar") && (String.copyValueOf(passwordField.getPassword())).equals("123")) {
            StudentUI student = new StudentUI();
            student.setTitle("Student Page");
            student.setVisible(true);

        } else if (choice.getSelectedItem().equals(values[1]) && username.getText().equals("Sir") && (String.copyValueOf(passwordField.getPassword())).equals("123")) {
            InstructorUI frame = new InstructorUI();
            frame.setTitle("Instructor Page");
            frame.setVisible(true);

        } else if (choice.getSelectedItem().equals(values[2]) && username.getText().equals("Admin") && (String.copyValueOf(passwordField.getPassword())).equals("123")) {

            AdminUI frame = new AdminUI();
            frame.setTitle("Admin Page");
            frame.setVisible(true);

        } else
            JOptionPane.showMessageDialog(
                    null,
                    "Wrong username or password or wrong choice of user type try again ! ",
                    "Failure !!",
                    JOptionPane.INFORMATION_MESSAGE);
    }

    private void loginButtonActionPerformed(java.awt.event.ActionEvent evt) {
        checkIn();
    }

    private void clearButtonActionPerformed(java.awt.event.ActionEvent evt) {
        username.setText("");
        passwordField.setText("");
    }

