    public String getUserName() {
        return username.getText();
    }

    public String getUserType() {
        String userType = null;
        if (choice.getSelectedIndex() == 0)
            userType = values[0];
        else if (choice.getSelectedIndex() == 1)
            userType = values[1];
        else if (choice.getSelectedIndex() == 2)
            userType = values[2];
        return userType;

    }



